import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const HomePage = () => {
  // Animation state
  const [isVisible, setIsVisible] = useState({
    hero: false,
    templates: false,
    features: false,
    howItWorks: false,
    testimonials: false,
    cta: false
  });

  // Intersection observer setup
  useEffect(() => {
    const observerOptions = {
      threshold: 0.2,
      rootMargin: '-50px'
    };

    const observerCallback = (entries) => {
      entries.forEach(entry => {
        if (entry.isIntersecting) {
          setIsVisible(prev => ({ ...prev, [entry.target.id]: true }));
        }
      });
    };

    const observer = new IntersectionObserver(observerCallback, observerOptions);
    
    // Observe all sections
    const sections = document.querySelectorAll('.animate-section');
    sections.forEach(section => observer.observe(section));

    return () => sections.forEach(section => observer.unobserve(section));
  }, []);

  // Sample templates for preview
  const featuredTemplates = [
    { 
      id: 'modern', 
      name: 'Modern', 
      description: 'Clean and minimal design for a contemporary look',
      imgSrc: '/assets/templates/modern-thumb.jpg',
      color: 'bg-blue-600'
    },
    { 
      id: 'professional', 
      name: 'Professional', 
      description: 'Traditional layout for corporate environments',
      imgSrc: '/assets/templates/professional-thumb.jpg',
      color: 'bg-gray-700'
    },
    { 
      id: 'creative', 
      name: 'Creative', 
      description: 'Unique design for creative fields and portfolios',
      imgSrc: '/assets/templates/creative-thumb.jpg',
      color: 'bg-purple-600'
    },
    { 
      id: 'pro-profile', 
      name: 'ProProfile', 
      description: 'Corporate style with dark sidebar for impact',
      imgSrc: '/assets/templates/pro-profile-thumb.jpg',
      color: 'bg-emerald-700'
    },
  ];

  // Features list
  const features = [
    {
      title: 'Professional Templates',
      description: 'Choose from a variety of expertly designed templates that make your resume stand out',
      icon: (
        <svg className="w-10 h-10 text-primary-500" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
        </svg>
      ),
    },
    {
      title: 'Real-time Preview',
      description: 'See exactly how your resume will look as you build it with our instant preview feature',
      icon: (
        <svg className="w-10 h-10 text-primary-500" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 12a3 3 0 11-6 0 3 3 0 016 0z" />
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M2.458 12C3.732 7.943 7.523 5 12 5c4.478 0 8.268 2.943 9.542 7-1.274 4.057-5.064 7-9.542 7-4.477 0-8.268-2.943-9.542-7z" />
        </svg>
      ),
    },
    {
      title: 'ATS-Optimized',
      description: 'Our templates are designed to pass through Applicant Tracking Systems with ease',
      icon: (
        <svg className="w-10 h-10 text-primary-500" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5H7a2 2 0 00-2 2v12a2 2 0 002 2h10a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2m-3 7h3m-3 4h3m-6-4h.01M9 16h.01" />
        </svg>
      ),
    },
    {
      title: 'Expert Guidance',
      description: 'Get helpful tips and suggestions to improve your resume and increase your chances of getting hired',
      icon: (
        <svg className="w-10 h-10 text-primary-500" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9.663 17h4.673M12 3v1m6.364 1.636l-.707.707M21 12h-1M4 12H3m3.343-5.657l-.707-.707m2.828 9.9a5 5 0 117.072 0l-.548.547A3.374 3.374 0 0014 18.469V19a2 2 0 11-4 0v-.531c0-.895-.356-1.754-.988-2.386l-.548-.547z" />
        </svg>
      ),
    },
  ];

  // Process steps
  const steps = [
    {
      number: '01',
      title: 'Select a Template',
      description: 'Choose from our collection of professional templates designed for different industries and career levels',
    },
    {
      number: '02',
      title: 'Fill in Your Details',
      description: 'Our intuitive editor makes it easy to add your personal information, experience, skills, and more',
    },
    {
      number: '03',
      title: 'Customize & Download',
      description: 'Personalize fonts, colors, and layout, then download your resume in PDF format ready to send to employers',
    },
  ];

  // Testimonials
  const testimonials = [
    {
      quote: "ResuMate helped me create a professional resume that got me interviews at top tech companies. The templates are clean and ATS-friendly.",
      author: "Sarah Johnson",
      title: "Software Engineer",
      company: "Google",
      avatar: "/assets/testimonials/avatar1.jpg",
    },
    {
      quote: "I was struggling with my resume for months until I found ResuMate. The guidance and templates helped me land my dream job.",
      author: "David Chen",
      title: "Marketing Director",
      company: "Amazon",
      avatar: "/assets/testimonials/avatar2.jpg",
    },
    {
      quote: "The real-time preview feature saved me so much time. I could see exactly how my resume would look while I was building it.",
      author: "Maria Rodriguez",
      title: "UX Designer",
      company: "Facebook",
      avatar: "/assets/testimonials/avatar3.jpg",
    },
  ];

  // FAQ items
  const faqItems = [
    {
      question: "Is ResuMate free to use?",
      answer: "We offer both free and premium templates. You can create and download resumes with our free templates, while our premium templates offer additional features and designs for a small fee."
    },
    {
      question: "Can I download my resume in different formats?",
      answer: "Yes, you can download your resume in PDF format, which is the industry standard and works well for both digital applications and printing."
    },
    {
      question: "Are the templates ATS-friendly?",
      answer: "Absolutely! All our templates are designed to pass through Applicant Tracking Systems with ease, ensuring your resume gets seen by hiring managers."
    },
    {
      question: "Can I create multiple resumes?",
      answer: "Yes, you can create and save multiple resumes for different job applications or industries. This helps you tailor your resume to specific job requirements."
    }
  ];

  return (
    <div className="min-h-screen overflow-hidden bg-gradient-to-b from-gray-50 to-gray-100 font-sans">
      {/* Hero Section */}
      <section id="hero" className="relative overflow-hidden animate-section">
        {/* Background Elements */}
