            <p className="text-xl text-gray-600 max-w-3xl mx-auto">
              Choose from our collection of ATS-friendly templates designed to impress employers
            </p>
          </div>
          
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8">
            {featuredTemplates.map((template, index) => (
              <Link 
                key={index}
                to={`/templates/${template.id}`} 
                className={`group transition-all duration-700 transform ${isVisible.templates ? 'translate-y-0 opacity-100' : 'translate-y-10 opacity-0'}`}
                style={{ transitionDelay: `${index * 150}ms` }}
              >
                <div className="bg-white rounded-xl overflow-hidden shadow-lg hover:shadow-xl transition-shadow duration-300">
                  <div className={`h-2 w-full ${template.color}`}></div>
                  <div className="p-6">
                    <h3 className="text-lg font-semibold text-gray-900 mb-2">{template.name}</h3>
                    <p className="text-gray-600 text-sm mb-4">{template.description}</p>
                    <div className="relative aspect-[3/4] rounded-lg overflow-hidden bg-gray-100">
                      <img 
                        src={template.imgSrc || `/assets/templates/placeholder.jpg`} 
                        alt={`${template.name} Template`} 
                        className="w-full h-full object-cover object-top group-hover:scale-105 transition-transform duration-500"
