            <Link 
              to="/templates" 
              className="btn border border-primary-600 text-primary-600 hover:bg-primary-50 px-8 py-3 rounded-lg inline-flex items-center gap-2 transition-all duration-300"
            >
              View All Templates
              <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
              </svg>
            </Link>
          </div>
        </div>
