import React from 'react';
import './TemplateSelector.css';

const TemplateSelector = ({ selectedTemplate, onTemplateChange }) => {
  const templates = [
    { 
      id: 'modern',
      name: 'Modern',
      description: 'Clean, professional layout with section dividers and bullet points'
    },
    { 
      id: 'classic',
      name: 'Classic',
      description: 'Traditional format with centered header and horizontal rules'
    },
    { 
      id: 'minimal',
      name: 'Minimal',
      description: 'Simple, streamlined design with minimal formatting'
    }
  ];

  return (
    <div className="template-selection">
      <h3>Choose Resume Template</h3>
      
      <div className="template-options">
        {templates.map(template => (
          <div 
            key={template.id}
            className={`template-option ${selectedTemplate === template.id ? 'selected' : ''}`}
            onClick={() => onTemplateChange(template.id)}
          >
            <h4>{template.name}</h4>
            <p>{template.description}</p>
          </div>
        ))}
      </div>
      

    </div>
  );
};

export default TemplateSelector;
