import React, { useState, useEffect } from 'react';
import ImageCropper from './ImageCropper';
import './ResumeForm.css';

const skillSuggestions = [
  'Java', 'Python', 'HTML', 'CSS', 'JavaScript', 'React', 'Node.js', 'SQL', 'C++', 'Communication', 'Teamwork', 'Problem Solving', 'Leadership', 'Time Management', 'Project Management'
];

const atsKeywords = [
  'Software Development', 'Web Development', 'Data Analysis', 'Agile', 'Testing', 'Debugging', 'API', 'Version Control', 'Collaboration', 'Documentation', 'Deployment', 'Optimization'
];

const initialData = {
  name: '',
  email: '',
  phone: '',
  address: '',
  objective: '',
  education: [{ degree: '', institution: '', year: '', percentage: '' }],
  experience: [{ company: '', role: '', duration: '' }],
  projects: [{ title: '', description: '' }],
  skills: [],
  achievements: [''],
  profilePic: null
};

function ResumeForm({ onSubmit, onChange }) {
  const [data, setData] = useState(() => {
    const saved = localStorage.getItem('resumeData');
    return saved ? JSON.parse(saved) : initialData;
  });
  const [objectiveCount, setObjectiveCount] = useState(0);

  useEffect(() => {
    localStorage.setItem('resumeData', JSON.stringify(data));
    setObjectiveCount(data.objective.length);
    
    // Send data to parent component for live preview
    if (onChange) {
      onChange(data);
    }
  }, [data, onChange]);

  // Handlers for form fields
  const handleChange = (e) => {
    const { name, value } = e.target;
    setData((prev) => ({ ...prev, [name]: value }));
  };

  // Handlers for array fields
  const handleArrayChange = (field, idx, subfield, value) => {
    setData((prev) => {
      const arr = [...prev[field]];
      arr[idx][subfield] = value;
      return { ...prev, [field]: arr };
    });
  };

  const addArrayItem = (field, item) => {
    setData((prev) => ({ ...prev, [field]: [...prev[field], item] }));
  };

  const removeArrayItem = (field, idx) => {
    setData((prev) => {
      const arr = [...prev[field]];
      arr.splice(idx, 1);
      return { ...prev, [field]: arr };
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(data);
  };

  return (
    <form onSubmit={handleSubmit} className="resume-form card shadow-sm p-4">
      <h1 className="text-center mb-4">ResuMate - ATS-Friendly Resume Builder</h1>
      
      <h2 className="border-bottom pb-2 mb-3">Personal Information</h2>
      <div className="mb-3">
        <input name="name" className="form-control" value={data.name} onChange={handleChange} placeholder="Full Name" required />
      </div>
      <div className="row mb-3">
        <div className="col-md-6 mb-2 mb-md-0">
          <input name="email" className="form-control" value={data.email} onChange={handleChange} placeholder="Email" required />
        </div>
        <div className="col-md-6">
          <input name="phone" className="form-control" value={data.phone} onChange={handleChange} placeholder="Phone" required />
        </div>
      </div>
      <div className="mb-3">
        <input name="address" className="form-control" value={data.address} onChange={handleChange} placeholder="Address" />
      </div>

      <h2>Career Objective</h2>
      <div className="form-group">
        <textarea 
          name="objective" 
          className="form-control" 
          value={data.objective} 
          onChange={handleChange} 
          placeholder="Objective (Keep it concise and use relevant keywords)" 
          maxLength={200} 
          rows="4"
        />
      </div>
      <div className="word-count">Word count: {objectiveCount}/200</div>
      <div className="tips-box">
        Tip: Use keywords like {atsKeywords.join(', ')} for better ATS results.
      </div>

      <h2>Education</h2>
      {data.education.map((edu, idx) => (
        <div key={idx} className="section-item">
          <div className="form-group">
            <input placeholder="Degree" className="form-control" value={edu.degree} onChange={e => handleArrayChange('education', idx, 'degree', e.target.value)} />
          </div>
          <div className="form-group">
            <input placeholder="Institution" className="form-control" value={edu.institution} onChange={e => handleArrayChange('education', idx, 'institution', e.target.value)} />
          </div>
          <div className="form-row">
            <div className="form-col">
              <input placeholder="Year" className="form-control" value={edu.year} onChange={e => handleArrayChange('education', idx, 'year', e.target.value)} />
            </div>
            <div className="form-col">
              <input placeholder="Percentage/GPA" className="form-control" value={edu.percentage} onChange={e => handleArrayChange('education', idx, 'percentage', e.target.value)} />
            </div>
          </div>
          <button type="button" className="btn btn-remove" onClick={() => removeArrayItem('education', idx)}>Remove</button>
        </div>
      ))}
      <button type="button" className="btn btn-add" onClick={() => addArrayItem('education', { degree: '', institution: '', year: '', percentage: '' })}>+ Add Education</button>

      <h2>Experience</h2>
      {data.experience.map((exp, idx) => (
        <div key={idx} className="section-item">
          <div className="form-group">
            <input placeholder="Company" className="form-control" value={exp.company} onChange={e => handleArrayChange('experience', idx, 'company', e.target.value)} />
          </div>
          <div className="form-group">
            <input placeholder="Role" className="form-control" value={exp.role} onChange={e => handleArrayChange('experience', idx, 'role', e.target.value)} />
          </div>
          <div className="form-group">
            <input placeholder="Duration (e.g., Jan 2020 - Dec 2021)" className="form-control" value={exp.duration} onChange={e => handleArrayChange('experience', idx, 'duration', e.target.value)} />
          </div>
          <button type="button" className="btn btn-remove" onClick={() => removeArrayItem('experience', idx)}>Remove</button>
        </div>
      ))}
      <button type="button" className="btn btn-add" onClick={() => addArrayItem('experience', { company: '', role: '', duration: '' })}>+ Add Experience</button>

      <h2>Projects</h2>
      {data.projects.map((proj, idx) => (
        <div key={idx} className="section-item">
          <div className="form-group">
            <input placeholder="Title" className="form-control" value={proj.title} onChange={e => handleArrayChange('projects', idx, 'title', e.target.value)} />
          </div>
          <div className="form-group">
            <textarea placeholder="Description" className="form-control" rows="2" value={proj.description} onChange={e => handleArrayChange('projects', idx, 'description', e.target.value)} />
          </div>
          <button type="button" className="btn btn-remove" onClick={() => removeArrayItem('projects', idx)}>Remove</button>
        </div>
      ))}
      <button type="button" className="btn btn-add" onClick={() => addArrayItem('projects', { title: '', description: '' })}>+ Add Project</button>

      <h2>Achievements</h2>
      {data.achievements.map((ach, idx) => (
        <div key={idx} className="section-item">
          <div className="form-group">
            <input placeholder="Achievement" className="form-control" value={ach} onChange={e => {
              const arr = [...data.achievements];
              arr[idx] = e.target.value;
              setData(prev => ({ ...prev, achievements: arr }));
            }} />
          </div>
          <button type="button" className="btn btn-remove" onClick={() => removeArrayItem('achievements', idx)}>Remove</button>
        </div>
      ))}
      <button type="button" className="btn btn-add" onClick={() => addArrayItem('achievements', '')}>+ Add Achievement</button>

      <h2>Skills</h2>
      <div className="form-group">
        <input 
          placeholder="Comma separated skills" 
          className="form-control" 
          value={data.skills.join(', ')} 
          onChange={e => setData(prev => ({ ...prev, skills: e.target.value.split(',').map(s => s.trim()).filter(Boolean) }))} 
        />
      </div>
      <div className="skill-suggestions">
        Suggestions: {skillSuggestions.map(skill => (
          <span className="skill-tag" key={skill} onClick={() => {
            if (!data.skills.includes(skill)) setData(prev => ({ ...prev, skills: [...prev.skills, skill] }));
          }}>{skill}</span>
        ))}
      </div>

      <h2>Profile Picture (Optional)</h2>
      <ImageCropper
        onImageCrop={(croppedImage) => setData(prev => ({ ...prev, profilePic: croppedImage }))}
      />
      
      <button type="submit" className="btn btn-primary btn-lg mt-4 w-100">Generate Resume</button>
    </form>
  );
}

export default ResumeForm;
